var gameOver = function(game){
};

var ownY;
var enemyY;

function actionOnClick () {
    game.state.start('mpLogin');
}

gameOver.prototype = {
    init: function(ownEndY, enemyEndY) {
        console.log("Game ended with ownEndY, enemyEndY: " + ownEndY + " " + enemyEndY);
		ownY = ownEndY;
        enemyY = enemyEndY;
	},
    preload: function() {
        game.load.spritesheet('button', 'assets/buttons/button_sprite_sheet.png', 193, 71);
    },
	create: function () {
        game.stage.disableVisibilityChange = true;
        game.stage.backgroundColor = "#0c9fc7";
        
        var gradientBackground = game.add.bitmapData(800, 600);
        var gradient=gradientBackground.context.createLinearGradient(0,0,0,500);
        gradient.addColorStop(0,"white");
        gradient.addColorStop(1,"green");
        gradientBackground.context.fillStyle=gradient;
        gradientBackground.context.fillRect(0,0,800,580);
        gradient=gradientBackground.context.createLinearGradient(0,580,0,600);
        gradient.addColorStop(0,"green");
        gradient.addColorStop(1,"black");
        gradientBackground.context.fillStyle=gradient;
        gradientBackground.context.fillRect(0,580,800,20);
        game.add.sprite(0, 0, gradientBackground);
        
        newGameButton = game.add.button(game.world.centerX - 95, 300, 'button', actionOnClick, this, 2, 1, 0);
        //newGameButton.onInputOut.add(out, this);
        
        txt = this.game.add.text(game.world.centerX,100, "Game over!\nYour score:" + ownY + "\nEnemy score:" + enemyY, {font: "30px Arial", fill: "#ffffff", stroke: '#000000', strokeThickness: 3});
        txt.wordWrap = true;
        txt.wordWrapWidth = (0.95 * this.game.world.width);

	},
    
}