var stompClient = null;

function setConnected(connected) {
    $("#connect").prop("disabled", connected);
    $("#disconnect").prop("disabled", !connected);
    if (connected) {
        $("#conversation").show();
    }
    else {
        $("#conversation").hide();
    }
    $("#greetings").html("");
}

function serverOutPutHandler(serverJson) {
    // console.log("--------------------------------" + serverJson.option)
    if(serverJson.option === "gameStartByRequest") {
        console.log("gameStartByRequest");
        if(!gameStarted) {
            gameStarted = true;
            joinGame();
        }
    } else if (serverJson.option === "gameRequest") {
        console.log("gameRequest");
        if(!gameStarted & serverJson.id != playerId) {
            playerRequestStartGame(playerName, playerId);
        }
               
    } else if (serverJson.option === "movementUpdate") {
        console.log("movementUpdate");
           if(gameStarted) {
                movePlayers(serverJson);
           }
    }
}

function connect() {
    console.log("connecting socket");
    var socket = new SockJS('http://localhost:8080/socket');

    stompClient = Stomp.over(socket);
    stompClient.connect({}, function (frame) {

        //setConnected(true);
        console.log('Connected: ' + frame);
        stompClient.subscribe('/game/play', function (greeting) {
            console.log("sub: " + JSON.parse(greeting.body));
            //showGreeting(JSON.parse(greeting.body).cake);
            serverOutPutHandler(JSON.parse(greeting.body));
        });
    });
}

function disconnect() {
    if (stompClient !== null) {
        stompClient.disconnect();
    }
    setConnected(false);
    console.log("Disconnected");
}


function playerRequestGame(name, id) {
    console.log("1111");
    var i = JSON.stringify({'name' : name, 'id' : id});
	stompClient.send("/app/requestGame", {}, i);
}

function playerRequestStartGame(name, id) {
    console.log("2222");
    var i = JSON.stringify({'name' : name, 'id' : id});
	stompClient.send("/app/requestStartGame", {}, i);
}

function updateMovementInput(name, id, move, jump) {
    console.log("3333");
    var i = JSON.stringify({'name' : name, 'id' : id, 'move' : move, 'jump': jump});
	stompClient.send("/app/movementUpdate", {}, i);
}


$(function () {
    $("form").on('submit', function (e) {
        e.preventDefault();
    });
    $( "#connect" ).click(function() { connect(); });
    $( "#disconnect" ).click(function() { disconnect(); });
    $( "#send" ).click(function() { sendName(); });
});








