function fire()
{
    var bulletSpeed = 500;
    var bulletSpacing = 400;
    var bullet = bullets.getFirstExists(false);
    if(game.time.now > nextFire)
    {
             if (bullet)
        {
             var bulletOffset = Math.sin(game.math.degToRad(player.angle));
            bullet.reset(player.x -7, player.y + 40);
            bullet.angle = player.angle;
           if (playerLeft == false)
           {
               game.physics.arcade.velocityFromAngle(bullet.angle, bulletSpeed, bullet.body.velocity);}
            if (playerLeft == true)
                {
                    game.physics.arcade.velocityFromAngle(bullet.angle - 180, bulletSpeed, bullet.body.velocity);
                }
            //bullet.body.velocity.x += player.body.velocity.x;

           nextFire = game.time.now + bulletSpacing;
        }
    }
}

function checkForPlayer(id) {
    if (players.hasOwnProperty(id)) {
        console.log("cake and glory ID");
        return true;
    }
    return false;
}

function createPlayer() {
    console.log("player created");
    var player = game.add.sprite(32, game.world.height - 150, 'dude');

    game.physics.arcade.enable(player);

    player.body.bounce.y = 0.2;
    player.body.gravity.y = 300;
    player.body.collideWorldBounds = true;

    player.animations.add('left', [0, 1, 2, 3], 10, true);
    player.animations.add('right', [5, 6, 7, 8], 10, true);
    return player;
}


function movePlayers(serverJson) {
    console.log("enemy id: " + enemyID);
    console.log("incoming ID: " + serverJson.id);
    /*
    if(!serverJson.id == playerId && !serverJson.id == enemyID) {
       return;
    }
    */
    
    console.log("******** ID: " + serverJson.id);
    for(var key in players){
      console.log("key: " + key);
      /* use key/value for intended purpose */
    }
    /*
    if(!checkForPlayer(serverJson.id)) {
        console.log("creating new player");
        players[serverJson.id] = createPlayer();
    }
    */
    
    
    var hitPlatform = game.physics.arcade.collide(players[serverJson.id], platforms);
    console.log("platform object: " + platforms);
    hitPlatform = true;
    // game.physics.arcade.collide(stars, platforms);

    game.physics.arcade.overlap(players[serverJson.id], null, this);

    //  Reset speed to 0
    players[serverJson.id].body.velocity.x = 0;
    
    if (serverJson.move == "left")
    {
        //  Move to the left
        players[serverJson.id].body.velocity.x = -150;

        players[serverJson.id].animations.play('left');
    }
    else if (serverJson.move == "right")
    {
        //  Move to the right
        players[serverJson.id].body.velocity.x = 150;
        console.log("moving right");
        players[serverJson.id].animations.play('right');
    }
    else
    {
        //  Stand still
        players[serverJson.id].animations.stop();

        players[serverJson.id].frame = 4;
    }

    //  Allow the player to jump if they are touching the ground.
    if (serverJson.jump == "true" && players[serverJson.id].body.touching.down && hitPlatform)
    {
        players[serverJson.id].body.velocity.y = -350;
    }
    
}

