let stompClient = null;

function setConnected(connected) {
    $("#connect").prop("disabled", connected);
    $("#disconnect").prop("disabled", !connected);
    if (connected) {
        $("#conversation").show();
    }
    else {
        $("#conversation").hide();
    }
    $("#greetings").html("");
}

function serverOutPutHandler(serverJson) {
    if(serverJson.option === "gameStartByRequest") {
        if(!gameStarted) {
            gameStarted = true;
        }
    } else if (serverJson.option === "gameRequest") {
        if(!gameStarted & serverJson.id != playerId) {
            playerRequestStartGame(playerName, playerId);
        }
               
    } else if (serverJson.option === "movementUpdate") {
       if(gameStarted) {
            movePlayers(serverJson);
       }
    }
}

function connect() {
    console.log("connecting socket");
    let socket = new SockJS('http://localhost:8080/socket');

    stompClient = Stomp.over(socket);
    stompClient.connect({}, function (frame) {
        stompClient.subscribe('/game/play', function (socketMsg) {
            serverOutPutHandler(JSON.parse(socketMsg.body));
        });
    });
}

function disconnect() {
    if (stompClient !== null) {
        stompClient.disconnect();
    }
    setConnected(false);
    console.log("Disconnected");
}


function playerRequestGame(name, id) {
    let i = JSON.stringify({'name' : name, 'id' : id});
	stompClient.send("/app/requestGame", {}, i);
}

function playerRequestStartGame(name, id) {
    let i = JSON.stringify({'name' : name, 'id' : id});
	stompClient.send("/app/requestStartGame", {}, i);
}

function updateMovementInput(name, id, move, jump) {
    let i = JSON.stringify({'name' : name, 'id' : id, 'move' : move, 'jump': jump});
	stompClient.send("/app/movementUpdate", {}, i);
}

